mapper.insert:
INSERT INTO TBL_USER_ROLE
(
    EMP_ID,
    ROLE_ID
)
VALUES
    (
        #{empId},
        #{roleId}
    )

mapper.select:
SELECT EMP_ID
FROM TBL_EMPLOYEE
WHERE EMP_SABUN = #{emp_sabun};

SELECT ROLE_ID
FROM TBL_ROLE
WHERE ROLE_NAME = #{newGrade};

mapper.update:
update TBL_EMPLOYEE
set EMP_STATUS = #{empStatus} , EMP_GRADE= #{empGrade}
where EMP_ID = #{empId}

update TBL_EMPLOYEE
set EMP_VAC = #{empVac}
where EMP_ID = #{empId}
