mapper.select:
SELECT EMP_ID, EMP_SABUN, EMP_PWD, EMP_NAME, EMP_DEPT, EMP_GRADE, EMP_BIRTH, EMP_PHONE, EMP_ADDR, EMP_ZIPCODE,EMP_DETAIL_ADDR,EMP_EMAIL,EMP_STATUS,EMP_FIRST_DT,EMP_LAST_DT,EMP_VAC
FROM TBL_EMPLOYEE
WHERE EMP_SABUN = #{empSabun} AND EMP_PWD = #{empPwd};

SELECT EMP_ID, EMP_SABUN, EMP_NAME, EMP_DEPT,EMP_GRADE
FROM TBL_EMPLOYEE
WHERE EMP_DEPT = #{empDept} AND EMP_GRADE = #{empGrade};
