mapper.select:
SELECT v.VAC_ID,e.EMP_NAME,e.EMP_DEPT,e.EMP_GRADE,v.VAC_TYPE,v.VAC_START_DATE,v.VAC_END_DATE,v.VAC_REG_DATE
FROM TBL_EMPLOYEE e JOIN TBL_VACATION v
ON e.EMP_ID=v.EMP_ID;

mapper.update:
UPDATE TBL_VACATION
SET VAC_STATUS = '승인완료'
WHERE VAC_ID = #{vacId}

UPDATE TBL_VACATION
SET VAC_STATUS = '승인반려'
WHERE VAC_ID = #{vacId}
